contracts/facets/BestOfFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {LibTBG} from "../libraries/LibTurnBasedGame.sol";
import {IBestOf} from "../interfaces/IBestOf.sol";

import {IERC1155Receiver} from "../interfaces/IERC1155Receiver.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {IRankToken} from "../interfaces/IRankToken.sol";
import "../abstracts/DiamondReentrancyGuard.sol";
import {LibBestOf} from "../libraries/LibBestOf.sol";
import {LibCoinVending} from "../libraries/LibCoinVending.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../abstracts/draft-EIP712Diamond.sol";

contract BestOfFacet is IBestOf, IERC1155Receiver, DiamondReentrancyGuard, IERC721Receiver, EIP712 {
    using LibTBG for LibTBG.GameInstance;
    using LibTBG for uint256;
    using LibTBG for LibTBG.GameSettings;
    using LibBestOf for uint256;

    event VoteSubmitted(
        uint256 indexed gameId,
        uint256 indexed turn,
        address indexed player,
        bytes32[3] votesHidden,
        bytes proof
    );

    function checkSignature(bytes memory message, bytes memory signature, address account) private view returns (bool) {
        bytes32 typedHash = _hashTypedDataV4(keccak256(message));
        return SignatureChecker.isValidSignatureNow(account, typedHash, signature);
    }

    function _isValidSignature(
        bytes memory message,
        bytes memory signature,
        address account
    ) private view returns (bool) {
        return checkSignature(message, signature, account);
    }

    function BOGStorage() internal pure returns (BOGSettings storage bog) {
        bytes32 position = LibTBG.getDataStorage();
        assembly {
            bog.slot := position
        }
    }

    function createGame(address gameMaster, uint256 gameId, uint256 gameRank) public payable nonReentrant {
        LibBestOf.enforceIsInitialized();
        BOGSettings storage settings = BOGStorage();
        gameId.createGame(gameMaster);
        // LibBestOf.fulfillTokenRequirement(msg.sender, address(this), gameId, settings.newGameReq);
        require(gameRank != 0, "game rank not specified");
        require(msg.value >= settings.gamePrice, "Not enough payment");
        LibBestOf.fulfillRankRq(msg.sender, address(this), gameRank, false);
        BOGInstance storage game = gameId.getGameStorage();
        game.createdBy = msg.sender;
        settings.numGames += 1;
        game.rank = gameRank;

        IRankToken rankTokenContract = IRankToken(settings.rankTokenAddress);
        rankTokenContract.mint(address(this), 1, gameRank + 1, "");
        rankTokenContract.mint(address(this), 3, gameRank, "");

        LibCoinVending.ConfigPosition memory emptyConfig;
        LibCoinVending.configure(bytes32(gameId), emptyConfig);

        emit gameCreated(gameId, gameMaster, msg.sender, gameRank);
    }

    function createGame(address gameMaster, uint256 gameRank) public payable {
        LibBestOf.enforceIsInitialized();
        BOGSettings storage settings = BOGStorage();
        createGame(gameMaster, settings.numGames + 1, gameRank);
    }

    function cancelGame(uint256 gameId) public nonReentrant {
        gameId.enforceGameExists();
        LibBestOf.enforceIsGameCreator(gameId);
        require(!gameId.hasStarted(), "Game already has started");
        address[] memory players = gameId.getPlayers();
        for (uint256 i = 0; i < players.length; i++) {
            gameId.removeAndUnlockPlayer(players[i]);
            LibCoinVending.refund(bytes32(gameId), players[i]);
            emit PlayerLeft(gameId, players[i]);
        }
        gameId.closeGame();
        emit GameClosed(gameId);
    }

    function leaveGame(uint256 gameId) public nonReentrant {
        gameId.removeAndUnlockPlayer(msg.sender);
        LibCoinVending.refund(bytes32(gameId), msg.sender);
        emit PlayerLeft(gameId, msg.sender);
    }

    function openRegistration(uint256 gameId) public {
        LibBestOf.enforceIsGameCreator(gameId);
        gameId.enforceIsPreRegistrationStage();
        gameId.openRegistration();
        emit RegistrationOpen(gameId);
    }

    // function getCreateGameRequirements() public view returns (TokenAction memory) {
    //     BOGSettings storage settings = BOGStorage();
    //     return settings.newGameReq;
    // }

    function joinGame(uint256 gameId) public payable nonReentrant {
        BOGInstance storage game = gameId.getGameStorage();
        gameId.enforceGameExists();
        LibCoinVending.fund(bytes32(gameId));
        LibBestOf.fulfillRankRq(msg.sender, address(this), game.rank, true);
        gameId.addPlayer(msg.sender);
        emit PlayerJoined(gameId, msg.sender);
    }

    function startGame(uint256 gameId) public {
        gameId.enforceGameExists();
        gameId.startGame();
        emit GameStarted(gameId);
    }

    //
    /* Proof is GM signed message formulated as:
    keccak256(
        abi.encode(voteHidden[1], voteHidden[2], voteHidden[3],playerTurnSalt)
    )

    where playerTurnSalt is :
    keccak256(abi.encode(msg.sender,turnSalt))

    Where turnSalt is:
    turnSalt = keccak256(abi.encode(gameId,turn,seed))

    Where seed is secret of game master which it never reveals.

    and voteHidden[i] = keccak256(abi.encode(proposerHidden,playerTurnSalt))

    where proposerHidden is

    proposerHidden = keccak256(abi.encode(playerAddress, turnSalt))

    Therefore secret voting can be revealed by making turnSalt publiclly availible.

    Having that, one can decode all hidden messages in local enviroment.


    it is game master responsibility to check and sign only such message where
    voteHidden1-3 are valid and can be decoded as

*/
    function submitVote(
        uint256 gameId,
        bytes32[3] memory votesHidden,
        bytes memory proof,
        bytes memory submitSignature
    ) public {
        gameId.enforceGameExists();
        gameId.enforceHasStarted();
        bytes memory message = abi.encode(
            LibBestOf._VOTE_SUBMIT_PROOF_TYPEHASH,
            gameId,
            gameId.getTurn(),
            votesHidden[0],
            votesHidden[1],
            votesHidden[2]
        );
        BOGInstance storage game = gameId.getGameStorage();
        _isValidSignature(message, submitSignature, gameId.getGM());
        require(!gameId.isGameOver(), "Game over");
        require(gameId.getTurn() > 1, "No proposals exist at turn 1: cannot vote");
        game.votesHidden[gameId.getTurn()][msg.sender].votedFor = votesHidden;
        game.votesHidden[gameId.getTurn()][msg.sender].proof = proof;
        gameId.playerMove(msg.sender);
        emit VoteSubmitted(gameId, gameId.getTurn(), msg.sender, votesHidden, proof);
    }

    function onERC1155Received(
        address operator,
        address,
        uint256,
        uint256,
        bytes calldata
    ) public view override returns (bytes4) {
        LibBestOf.enforceIsInitialized();
        if (operator == address(this)) {
            return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
        }
        return bytes4("");
    }

    function onERC1155BatchReceived(
        address operator,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external view override returns (bytes4) {
        LibBestOf.enforceIsInitialized();
        if (operator == address(this)) {
            return bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"));
        }
        return bytes4("");
    }

    function onERC721Received(
        address operator,
        address,
        uint256,
        bytes calldata
    ) external view override returns (bytes4) {
        LibBestOf.enforceIsInitialized();
        if (operator == address(this)) {
            return IERC721Receiver.onERC721Received.selector;
        }
        return bytes4("");
    }

    function getContractState() public view returns (ContractState memory) {
        BOGSettings storage settings = BOGStorage();
        LibTBG.GameSettings memory tbgSettings = LibTBG.getGameSettings();
        return (ContractState({BestOfState: settings, TBGSEttings: tbgSettings}));
    }

    function getTurn(uint256 gameId) public view returns (uint256) {
        return gameId.getTurn();
    }

    function getGM(uint256 gameId) public view returns (address) {
        return gameId.getGM();
    }

    function getScores(uint256 gameId) public view returns (address[] memory, uint256[] memory) {
        return gameId.getScores();
    }

    function isOvertime(uint256 gameId) public view returns (bool) {
        return gameId.isOvertime();
    }

    function isGameOver(uint256 gameId) public view returns (bool) {
        return gameId.isGameOver();
    }

    function getPlayersGame(address player) public view returns (uint256) {
        return LibTBG.getPlayersGame(player);
    }

    function isLastTurn(uint256 gameId) public view returns (bool) {
        return gameId.isLastTurn();
    }

    function isRegistrationOpen(uint256 gameId) public view returns (bool) {
        return gameId.isRegistrationOpen();
    }

    function gameCreator(uint256 gameId) public view returns (address) {
        return gameId.getGameStorage().createdBy;
    }

    function getGameRank(uint256 gameId) public view returns (uint256) {
        return gameId.getGameStorage().rank;
    }

    function getPlayers(uint256 gameId) public view returns (address[] memory) {
        return gameId.getPlayers();
    }

    function canStartGame(uint256 gameId) public view returns (bool) {
        return gameId.canStart();
    }
}

contracts/facets/gameMastersFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {LibArray} from "../libraries/LibArray.sol";
import {LibTBG} from "../libraries/LibTurnBasedGame.sol";
import {LibBestOf} from "../libraries/LibBestOf.sol";
import {IBestOf} from "../interfaces/IBestOf.sol";
import "../abstracts/DiamondReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../abstracts/draft-EIP712Diamond.sol";
import {RankToken} from "../tokens/RankToken.sol";
import {LibCoinVending} from "../libraries/LibCoinVending.sol";

contract GameMastersFacet is DiamondReentrancyGuard, EIP712 {
    using LibTBG for uint256;
    using LibBestOf for uint256;
    using LibTBG for LibTBG.GameInstance;

    event OverTime(uint256 indexed gameId);
    event LastTurn(uint256 indexed gameId);

    event TurnEnded(
        uint256 indexed gameId,
        uint256 indexed turn,
        address[] players,
        uint256[] scores,
        bytes32 indexed turnSalt,
        address[] voters,
        string[3][] votesRevealed
    );

    event ProposalSubmitted(
        uint256 indexed gameId,
        uint256 indexed turn,
        bytes32 hashedProposer,
        bytes proof,
        string proposal
    );
    event GameOver(uint256 indexed gameId, address[] indexed players, uint256[] indexed scores);

    function checkSignature(bytes memory message, bytes memory signature, address account) public view returns (bool) {
        bytes32 typedHash = _hashTypedDataV4(keccak256(message));
        return SignatureChecker.isValidSignatureNow(account, typedHash, signature);
    }

    function playerSalt(address player, bytes32 turnSalt) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(player, turnSalt));
    }

    function validateVote(uint256 gameId, address voter, string[3] memory vote, bytes32 turnSalt) public view {
        IBestOf.BOGInstance storage game = gameId.getGameStorage();
        bytes32 salt = playerSalt(voter, turnSalt);
        require(gameId.isPlayerInGame(voter), "Player not in that game");
        bytes memory message = abi.encode(
            LibBestOf._VOTE_PROOF_TYPEHASH,
            keccak256(abi.encodePacked(vote[0])),
            keccak256(abi.encodePacked(vote[1])),
            keccak256(abi.encodePacked(vote[2])),
            gameId,
            gameId.getTurn(),
            salt
        );
        bytes memory proof = game.votesHidden[gameId.getTurn()][voter].proof;
        require(_isValidSignature(message, proof, gameId.getGM()), "invalid signature");
        //make sure voter did not vote for himself
        bytes32 prevTurnPlayerSalt = keccak256(abi.encodePacked(voter, game.prevTurnSalt));
        for (uint256 i = 0; i < vote.length; i++) {
            require(
                !LibBestOf.compareStrings(
                    game
                    .proposals[gameId.getTurn() - 1][keccak256(abi.encodePacked(voter, prevTurnPlayerSalt))].proposal,
                    vote[i]
                ),
                "voted for himself"
            );
        }
    }

    function validateVotes(
        uint256 gameId,
        address[] memory voters,
        string[3][] memory votes,
        bytes32 turnSalt
    ) private view {
        for (uint256 i = 0; i < gameId.getPlayersNumber(); i++) {
            validateVote(gameId, voters[i], [votes[i][0], votes[i][1], votes[i][2]], turnSalt);
        }
    }

    function submitProposal(uint256 gameId, bytes32 proposerHidden, bytes memory proof, string memory proposal) public {
        LibBestOf.enforceIsGM(gameId);
        IBestOf.BOGInstance storage game = gameId.getGameStorage();
        gameId.enforceHasStarted();
        require(!gameId.isGameOver(), "Game over");
        require(!gameId.isLastTurn(), "Cannot propose in last turn");
        require(bytes(proposal).length != 0, "Cannot propose empty string");

        require(proposerHidden != bytes32(0), "proposerHidden cannot be empty");
        require(proof.length != 0, "proof cannot be empty");
        require(bytes(game.proposals[gameId.getTurn()][proposerHidden].proposal).length == 0, "Already proposed!");

        IBestOf.Proposal memory newProposal;
        newProposal.proposal = proposal;
        newProposal.proof = proof;
        game.proposals[gameId.getTurn()][proposerHidden] = newProposal;
        game.numProposals += 1;
        emit ProposalSubmitted(gameId, gameId.getTurn(), proposerHidden, proof, proposal);
    }

    function _isValidSignature(
        bytes memory message,
        bytes memory signature,
        address account
    ) private view returns (bool) {
        return checkSignature(message, signature, account);
    }

    function _endGame(
        uint256 gameId,
        address[] memory leaderboard,
        uint256[] memory scores,
        address[] memory players
    ) internal nonReentrant {
        IBestOf.BOGInstance storage game = gameId.getGameStorage();
        gameId.closeGame();
        emitRankRewards(gameId, leaderboard);

        for (uint256 i = 0; i < players.length; i++) {
            LibCoinVending.release(bytes32(gameId), game.createdBy, leaderboard[0], players[i]);
            LibBestOf.fulfillRankRq(address(this), players[i], game.rank, true);
        }
        emit GameOver(gameId, players, scores);
    }

    //Proposers order must be same as hidden proposal ordering in game.proposals
    function endTurn(
        uint256 gameId,
        bytes32 turnSalt,
        address[] memory voters,
        string[3][] memory votesRevealed
    ) public {
        LibBestOf.enforceIsGM(gameId);
        address[] memory players = gameId.getPlayers();
        uint256 turn = gameId.getTurn();
        uint256[] memory scores = new uint256[](players.length);

        IBestOf.BOGInstance storage game = gameId.getGameStorage();
        require(!gameId.isGameOver(), "Game over");
        gameId.enforceHasStarted();
        if (turn != 1) {
            require(gameId.canEndTurn() == true, "Cannot do this now");
        }
        if (!gameId.isLastTurn()) {
            require(
                (game.numProposals == gameId.getPlayers().length) || gameId.isTurnTimedOut(),
                "Some players still have time to propose"
            );
        }

        for (uint256 i = 0; i < players.length; i++) {
            require(gameId.isPlayerInGame(players[i]), "Proposer is not in the game");
            bytes32 playerPrevTurnSalt = keccak256(abi.encodePacked(players[i], game.prevTurnSalt));
            bytes32 prevTurnProposerHash = keccak256(abi.encodePacked(players[i], playerPrevTurnSalt));

            if ((turn != 1) && bytes(game.proposals[turn - 1][prevTurnProposerHash].proposal).length != 0) {
                //if proposal exsists
                bytes memory message = abi.encode(
                    LibBestOf._PROPOSAL_PROOF_TYPEHASH,
                    gameId,
                    turn - 1,
                    playerPrevTurnSalt,
                    keccak256(abi.encodePacked(game.proposals[turn - 1][prevTurnProposerHash].proposal))
                );
                require(
                    _isValidSignature(message, game.proposals[turn - 1][prevTurnProposerHash].proof, players[i]),
                    "Signature is wrong"
                );
                validateVotes(gameId, voters, votesRevealed, turnSalt);
                scores[i] =
                    gameId.getScore(players[i]) +
                    LibBestOf.getProposalScore(
                        gameId,
                        game.proposals[turn - 1][prevTurnProposerHash].proposal,
                        voters,
                        votesRevealed,
                        players[i]
                    );
                gameId.setScore(players[i], scores[i]);
            } else {
                //Player did not propose anything - his score stays same;
                //Unless there is still time to submit proposals
            }
        }
        (bool _isLastTurn, bool _isOvertime, bool _isGameOver, address[] memory leaderboard) = gameId.nextTurn();
        game.numProposals = 0;
        game.prevTurnSalt = turnSalt;
        emit TurnEnded(gameId, turn, players, scores, turnSalt, voters, votesRevealed);
        if (_isLastTurn && _isOvertime) {
            emit OverTime(gameId);
        }
        if (_isLastTurn) {
            emit LastTurn(gameId);
        }
        if (_isGameOver) {
            _endGame(gameId, leaderboard, scores, players);
        }
    }

    function emitRankRewards(uint256 gameId, address[] memory leaderboard) private {
        IBestOf.BOGInstance storage game = gameId.getGameStorage();
        IBestOf.BOGSettings storage settings = LibBestOf.BOGStorage();
        RankToken rankTokenContract = RankToken(settings.rankTokenAddress);
        rankTokenContract.safeTransferFrom(address(this), leaderboard[0], game.rank + 1, 1, "");
        rankTokenContract.safeTransferFrom(address(this), leaderboard[1], game.rank, 2, "");
        rankTokenContract.safeTransferFrom(address(this), leaderboard[2], game.rank, 1, "");
    }
}

contracts/facets/RequirementsFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import {LibTBG} from "../libraries/LibTurnBasedGame.sol";
import {LibCoinVending} from "../libraries/LibCoinVending.sol";
import {LibBestOf} from "../libraries/LibBestOf.sol";

contract RequirementsFacet {
    using LibTBG for uint256;
    using LibBestOf for uint256;
    using LibTBG for LibTBG.GameInstance;
    event RequirementsConfigured(uint256 indexed gameId, LibCoinVending.ConfigPosition config);

    function setJoinRequirements(uint256 gameId, LibCoinVending.ConfigPosition memory config) public {
        gameId.enforceIsGameCreator();
        gameId.enforceIsPreRegistrationStage();
        LibCoinVending.configure(bytes32(gameId), config);
        emit RequirementsConfigured(gameId, config);
    }

    function getJoinRequirements(uint256 gameId) public view returns (LibCoinVending.ConditionReturn memory) {
        return LibCoinVending.getPosition(bytes32(gameId));
    }

    function getJoinRequirementsByToken(
        uint256 gameId,
        address contractAddress,
        uint256 contractId,
        LibCoinVending.ContractTypes contractType
    ) public view returns (LibCoinVending.ContractCondition memory) {
        return LibCoinVending.getPositionByContract(bytes32(gameId), contractAddress, contractId, contractType);
    }
}

